    private void jButtonRemover1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonRemover1ActionPerformed
        JOptionPane.showConfirmDialog(this, "Tem certeza que deseja remover esse Cliente?", "Confirmação de exclusão", JOptionPane.OK_OPTION | JOptionPane.CANCEL_OPTION);
    }//GEN-LAST:event_jButtonRemover1ActionPerformed

    private void jButtonSalvar1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSalvar1ActionPerformed
        JOptionPane.showConfirmDialog(this, "Cliente já Existente! Deseja Substitui-lo?", "Confirmação de atualização", JOptionPane.OK_OPTION | JOptionPane.CANCEL_OPTION);
    }//GEN-LAST:event_jButtonSalvar1ActionPerformed

